import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axiosConfig";
import "./Profile.css";

function Profile() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [menuOpen, setMenuOpen] = useState(false);
  const [avatarFile, setAvatarFile] = useState(null); // file upload
  const [avatarPreview, setAvatarPreview] = useState(null); // preview ·∫£nh

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p.");
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get("/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
        setAvatarPreview(res.data.avatar || null);
      } catch (err) {
        console.error("L·ªói khi l·∫•y th√¥ng tin user:", err);
        if (err.response?.status === 401) {
          setError("Phi√™n ƒëƒÉng nh·∫≠p h·∫øt h·∫°n. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.");
          localStorage.removeItem("token");
        } else {
          setError("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleDeleteAccount = async () => {
    if (window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a t√†i kho·∫£n n√†y?")) {
      try {
        const token = localStorage.getItem("token");
        await axios.delete("/delete-account", {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert("T√†i kho·∫£n ƒë√£ b·ªã x√≥a.");
        localStorage.removeItem("token");
        navigate("/login");
      } catch (err) {
        console.error("X√≥a t√†i kho·∫£n th·∫•t b·∫°i:", err);
        alert("Kh√¥ng th·ªÉ x√≥a t√†i kho·∫£n.");
      }
    }
  };

  const toggleMenu = () => {
    setMenuOpen((prev) => !prev);
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setAvatarFile(file);

      // hi·ªÉn th·ªã preview
      const reader = new FileReader();
      reader.onload = (ev) => {
        setAvatarPreview(ev.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleUploadAvatar = async () => {
    if (!avatarFile) return;
    const formData = new FormData();
    formData.append("avatar", avatarFile);

    try {
      const token = localStorage.getItem("token");
      const res = await axios.post("/profile/avatar", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      alert("C·∫≠p nh·∫≠t ·∫£nh th√†nh c√¥ng!");
      setUser({ ...user, avatar: res.data.avatar });
      setAvatarFile(null);
    } catch (err) {
      console.error("Upload ·∫£nh th·∫•t b·∫°i:", err);
      alert("Kh√¥ng th·ªÉ upload ·∫£nh.");
    }
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="loading-card">ƒêang t·∫£i th√¥ng tin ng∆∞·ªùi d√πng...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-page">
        <div className="error-card">{error}</div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <header className="header">
        <div className="logo" onClick={() => navigate("/")}>
          üè† Trang ch·ªß
        </div>

        <div className="hamburger" onClick={toggleMenu}>
          <div className="bar"></div>
          <div className="bar"></div>
          <div className="bar"></div>

          {menuOpen && (
            <div className="menu">
              <p onClick={() => navigate("/edit-profile")}>‚úèÔ∏è Ch·ªânh s·ª≠a th√¥ng tin</p>
              <p onClick={() => navigate("/change-password")}>üîë Thay ƒë·ªïi m·∫≠t kh·∫©u</p>
              <p onClick={handleLogout}>üö™ ƒêƒÉng xu·∫•t</p>
              <p className="delete-account" onClick={handleDeleteAccount}>
                üóëÔ∏è X√≥a t√†i kho·∫£n
              </p>
            </div>
          )}
        </div>
      </header>

      <main className="profile-content">
        <div className="profile-card">
          <img
            src={avatarPreview || "https://cdn-icons-png.flaticon.com/512/3135/3135715.png"}
            alt="Avatar"
            className="avatar"
          />
          <h2>{user.name || "Kh√¥ng c√≥ t√™n"}</h2>
          <p className="email">{user.email}</p>
          <p className="role">
            Vai tr√≤: <span>{user.role}</span>
          </p>

          {/* Upload avatar */}
          <input type="file" accept="image/*" onChange={handleAvatarChange} />
          {avatarFile && (
            <button className="upload-btn" onClick={handleUploadAvatar}>
              C·∫≠p nh·∫≠t ·∫£nh
            </button>
          )}
        </div>
      </main>
    </div>
  );
}

export default Profile;
