import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./ForgotPassword.css"; // tái dùng CSS

function ResetPassword() {
  const navigate = useNavigate();
  const [token, setToken] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleReset = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setIsError(true);
      setMessage("⚠️ Mật khẩu xác nhận không khớp!");
      return;
    }

    setMessage("");
    setIsError(false);
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:3000/users/reset-password", {
        token,
        newPassword,
      });

      setMessage(res.data.message || "Đặt lại mật khẩu thành công!");
      setIsError(false);
      setTimeout(() => navigate("/login"), 2000);
    } catch (err) {
      console.error("❌ Lỗi đặt lại mật khẩu:", err);
      setIsError(true);
      setMessage(err.response?.data?.message || "Không thể đặt lại mật khẩu.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="forgot-wrapper">
      <div className="forgot-card">
        <h2>Đặt lại mật khẩu</h2>
        <form onSubmit={handleReset}>
          <label>Mã xác thực (token)</label>
          <input
            type="text"
            placeholder="Nhập mã token nhận từ email..."
            value={token}
            onChange={(e) => setToken(e.target.value)}
            required
          />

          <label>Mật khẩu mới</label>
          <input
            type="password"
            placeholder="Nhập mật khẩu mới..."
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            required
          />

          <label>Xác nhận mật khẩu mới</label>
          <input
            type="password"
            placeholder="Nhập lại mật khẩu..."
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />

          {message && (
            <p className={`forgot-message ${isError ? "error" : "success"}`}>
              {message}
            </p>
          )}

          <button type="submit" className="forgot-btn" disabled={loading}>
            {loading ? "Đang xử lý..." : "Cập nhật mật khẩu"}
          </button>
        </form>

        <p className="back-login" onClick={() => navigate("/login")}>
          ⬅️ Quay lại đăng nhập
        </p>
      </div>
    </div>
  );
}

export default ResetPassword;
