import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Sử dụng axios trực tiếp như trong Login.jsx
import "./Auth.css"; // Giữ file CSS chung

function Signup({ onLogin }) { // Thêm prop onLogin để đồng bộ với App.jsx
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taikhoan: "",
    name: "",
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(false); // Thêm trạng thái loading

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setIsError(false);
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:3000/api/users/signup", formData); // Điều chỉnh endpoint cho signup

      // Kiểm tra token và thông tin người dùng
      if (res.data && res.data.token) {
        // Lưu token và thông tin vào localStorage, tương tự Login.jsx
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("userId", res.data.user?.taikhoan);
        localStorage.setItem("isLoggedIn", "true");

        // Báo cho App biết đã đăng ký và đăng nhập
        if (onLogin) onLogin();

        setIsError(false);
        setMessage("🎉 " + (res.data.message || "Đăng ký thành công!"));

        // Điều hướng sang Profile
        setTimeout(() => navigate("/Profile"), 1200);
      } else {
        setIsError(true);
        setMessage(res.data.message || "Đăng ký thất bại!");
      }
    } catch (err) {
      setIsError(true);
      setMessage(err.response?.data?.message || "⚠️ Lỗi kết nối đến server!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-wrapper"> {/* Thay auth-wrapper thành login-wrapper để đồng bộ style */}
      <div className="login-card"> {/* Thay auth-card thành login-card */}
        <h2>Đăng ký tài khoản</h2>

        <form onSubmit={handleSubmit}>
          <label>Tên đăng nhập</label>
          <input
            type="text"
            name="taikhoan"
            placeholder="Nhập tên đăng nhập..."
            value={formData.taikhoan}
            onChange={handleChange}
            required
          />

          <label>Họ và tên</label>
          <input
            type="text"
            name="name"
            placeholder="Nhập họ và tên..."
            value={formData.name}
            onChange={handleChange}
          />

          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Nhập email..."
            value={formData.email}
            onChange={handleChange}
            required
          />

          <label>Mật khẩu</label>
          <input
            type="password"
            name="password"
            placeholder="Nhập mật khẩu..."
            value={formData.password}
            onChange={handleChange}
            required
          />

          {message && (
            <p className={`login-message ${isError ? "error" : "success"}`}>
              {message}
            </p>
          )}

          <button type="submit" className="login-btn" disabled={loading}>
            {loading ? "Đang đăng ký..." : "Đăng ký"}
          </button>
        </form>

        <p className="switch-text">
          Đã có tài khoản?{" "}
          <span className="switch-link" onClick={() => navigate("/login")}>
            Đăng nhập
          </span>
        </p>
      </div>
    </div>
  );
}

export default Signup;
