import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // S·ª≠ d·ª•ng axios tr·ª±c ti·∫øp nh∆∞ trong Login.jsx
import "./Auth.css"; // Gi·ªØ file CSS chung

function Signup({ onLogin }) { // Th√™m prop onLogin ƒë·ªÉ ƒë·ªìng b·ªô v·ªõi App.jsx
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taikhoan: "",
    name: "",
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(false); // Th√™m tr·∫°ng th√°i loading

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");
    setIsError(false);
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:3000/api/users/signup", formData); // ƒêi·ªÅu ch·ªânh endpoint cho signup

      // Ki·ªÉm tra token v√† th√¥ng tin ng∆∞·ªùi d√πng
      if (res.data && res.data.token) {
        // L∆∞u token v√† th√¥ng tin v√†o localStorage, t∆∞∆°ng t·ª± Login.jsx
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("userId", res.data.user?.taikhoan);
        localStorage.setItem("isLoggedIn", "true");

        // B√°o cho App bi·∫øt ƒë√£ ƒëƒÉng k√Ω v√† ƒëƒÉng nh·∫≠p
        if (onLogin) onLogin();

        setIsError(false);
        setMessage("üéâ " + (res.data.message || "ƒêƒÉng k√Ω th√†nh c√¥ng!"));

        // ƒêi·ªÅu h∆∞·ªõng sang Profile
        setTimeout(() => navigate("/Profile"), 1200);
      } else {
        setIsError(true);
        setMessage(res.data.message || "ƒêƒÉng k√Ω th·∫•t b·∫°i!");
      }
    } catch (err) {
      setIsError(true);
      setMessage(err.response?.data?.message || "‚ö†Ô∏è L·ªói k·∫øt n·ªëi ƒë·∫øn server!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-wrapper"> {/* Thay auth-wrapper th√†nh login-wrapper ƒë·ªÉ ƒë·ªìng b·ªô style */}
      <div className="login-card"> {/* Thay auth-card th√†nh login-card */}
        <h2>ƒêƒÉng k√Ω t√†i kho·∫£n</h2>

        <form onSubmit={handleSubmit}>
          <label>T√™n ƒëƒÉng nh·∫≠p</label>
          <input
            type="text"
            name="taikhoan"
            placeholder="Nh·∫≠p t√™n ƒëƒÉng nh·∫≠p..."
            value={formData.taikhoan}
            onChange={handleChange}
            required
          />

          <label>H·ªç v√† t√™n</label>
          <input
            type="text"
            name="name"
            placeholder="Nh·∫≠p h·ªç v√† t√™n..."
            value={formData.name}
            onChange={handleChange}
          />

          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Nh·∫≠p email..."
            value={formData.email}
            onChange={handleChange}
            required
          />

          <label>M·∫≠t kh·∫©u</label>
          <input
            type="password"
            name="password"
            placeholder="Nh·∫≠p m·∫≠t kh·∫©u..."
            value={formData.password}
            onChange={handleChange}
            required
          />

          {message && (
            <p className={`login-message ${isError ? "error" : "success"}`}>
              {message}
            </p>
          )}

          <button type="submit" className="login-btn" disabled={loading}>
            {loading ? "ƒêang ƒëƒÉng k√Ω..." : "ƒêƒÉng k√Ω"}
          </button>
        </form>

        <p className="switch-text">
          ƒê√£ c√≥ t√†i kho·∫£n?{" "}
          <span className="switch-link" onClick={() => navigate("/login")}>
            ƒêƒÉng nh·∫≠p
          </span>
        </p>
      </div>
    </div>
  );
}

export default Signup;
