import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import api from "./axiosConfig"; // Sử dụng api từ axiosConfig thay vì axios trực tiếp
import "./Auth.css";

function Signup({ onLogin }) { // Thêm prop onLogin để đồng bộ với App.jsx
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    taikhoan: "",
    name: "",
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const res = await api.post("/signup", formData); // Sử dụng api thay vì axios
      const { token, message } = res.data; // Giả định server trả về token và message

      // Lưu token vào localStorage
      if (token) {
        localStorage.setItem("authToken", token);
      }

      setIsError(false);
      setMessage("🎉 " + message);

      // Gọi onLogin để cập nhật trạng thái đăng nhập
      if (onLogin) {
        onLogin();
      }

      // Chuyển hướng đến trang profile thay vì login
      setTimeout(() => navigate("/profile"), 1200);
    } catch (err) {
      setIsError(true);
      if (err.response && err.response.data && err.response.data.message) {
        setMessage("❌ " + err.response.data.message);
      } else {
        setMessage("⚠️ Lỗi kết nối đến server!");
      }
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-card">
        <h2>Đăng ký tài khoản</h2>

        <form onSubmit={handleSubmit}>
          <label>Tên đăng nhập</label>
          <input
            type="text"
            name="taikhoan"
            placeholder="Nhập tên đăng nhập..."
            value={formData.taikhoan}
            onChange={handleChange}
            required
          />

          <label>Họ và tên</label>
          <input
            type="text"
            name="name"
            placeholder="Nhập họ và tên..."
            value={formData.name}
            onChange={handleChange}
          />

          <label>Email</label>
          <input
            type="email"
            name="email"
            placeholder="Nhập email..."
            value={formData.email}
            onChange={handleChange}
            required
          />

          <label>Mật khẩu</label>
          <input
            type="password"
            name="password"
            placeholder="Nhập mật khẩu..."
            value={formData.password}
            onChange={handleChange}
            required
          />

          {message && (
            <p className={`auth-message ${isError ? "error" : "success"}`}>
              {message}
            </p>
          )}

          <button type="submit" className="auth-btn">
            Đăng ký
          </button>
        </form>

        <p className="switch-text">
          Đã có tài khoản?{" "}
          <span className="switch-link" onClick={() => navigate("/login")}>
            Đăng nhập
          </span>
        </p>
      </div>
    </div>
  );
}

export default Signup;
