import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axiosConfig";
import "./Profile.css";

function Profile() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p.");
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get("/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
      } catch (err) {
        console.error("L·ªói khi l·∫•y th√¥ng tin user:", err);
        if (err.response?.status === 401) {
          setError("Phi√™n ƒëƒÉng nh·∫≠p h·∫øt h·∫°n. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.");
          localStorage.removeItem("token"); // x√≥a token h·∫øt h·∫°n
        } else {
          setError("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login"); // chuy·ªÉn SPA v·ªÅ login
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="loading-card">ƒêang t·∫£i th√¥ng tin ng∆∞·ªùi d√πng...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-page">
        <div className="error-card">{error}</div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <header className="header">
        <div className="logo" onClick={() => navigate("/")}>
          üè† Trang ch·ªß
        </div>
      </header>

      <main className="profile-content">
        <div className="profile-card">
          <img
            src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
            alt="Avatar"
            className="avatar"
          />
          <h2>{user.name || "Kh√¥ng c√≥ t√™n"}</h2>
          <p className="email">{user.email}</p>
          <p className="role">
            Vai tr√≤: <span>{user.role}</span>
          </p>
          <button onClick={handleLogout} className="logout-btn">
            ƒêƒÉng xu·∫•t
          </button>
        </div>
      </main>
    </div>
  );
}

export default Profile;
