import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Menu from "./component/Menu.jsx";
import Signup from "./component/Signup.jsx";
import Login from "./component/Login.jsx";
import Profile from "./component/Profile.jsx";
import QuanLyUser from "./component/QuanLyUser.jsx";
import api from "./axiosConfig";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  // ‚úÖ Gi·ªØ tr·∫°ng th√°i ƒëƒÉng nh·∫≠p khi reload (l∆∞u ·ªü localStorage)
  useEffect(() => {
    const loggedIn = localStorage.getItem("isLoggedIn") === "true";
    if (loggedIn) setIsLoggedIn(true);
  }, []);

  // ‚úÖ Khi ƒëƒÉng nh·∫≠p th√†nh c√¥ng t·ª´ Login.jsx
  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
  };

  // ‚úÖ Khi ƒëƒÉng xu·∫•t
  const handleLogout = async () => {
    try {
      await api.post("/logout");
    } catch (err) {
      console.warn("Logout failed:", err.message);
    } finally {
      setIsLoggedIn(false);
      localStorage.removeItem("isLoggedIn");
      window.location.href = "/login"; // üëâ Quay l·∫°i trang ƒëƒÉng nh·∫≠p
    }
  };

  return (
    <Router>
      <Menu isLoggedIn={isLoggedIn} onLogout={handleLogout} />

      <Routes>
        <Route
          path="/"
          element={isLoggedIn ? <Navigate to="/Profile" /> : <Navigate to="/Login" />}
        />

        <Route path="/Signup" element={<Signup />} />

        <Route path="/Login" element={<Login onLogin={handleLogin} />} />

        {/* ‚úÖ Ch·ªâ cho v√†o Profile n·∫øu ƒë√£ ƒëƒÉng nh·∫≠p */}
        <Route
          path="/Profile"
          element={isLoggedIn ? <Profile /> : <Navigate to="/Login" />}
        />

        {/* ‚úÖ T∆∞∆°ng t·ª± cho trang qu·∫£n l√Ω */}
        <Route
          path="/Quanly"
          element={isLoggedIn ? <QuanLyUser /> : <Navigate to="/Login" />}
        />

        {/* ‚úÖ N·∫øu nh·∫≠p sai ƒë∆∞·ªùng d·∫´n, t·ª± quay v·ªÅ */}
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
