import React, { useState } from "react";
import api from "../axiosConfig";
import "../App.css";

function Login({ onLogin, onSwitchToRegister }) {
  const [taikhoan, setTaikhoan] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const res = await api.post("/login", { taikhoan, password });

      // ✅ Nếu đăng nhập thành công: chuyển sang Profile
      if (res.data && res.data.success) {
        onLogin(res.data.userId); // Truyền ID người dùng về App
      } else {
        setError(res.data.message || "Đăng nhập thất bại!");
      }
    } catch (err) {
      setError(
        err.response?.data?.message || "⚠️ Không thể kết nối đến server!"
      );
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-card">
        <h2>Đăng nhập</h2>
        <form onSubmit={handleLogin}>
          <label>Tên tài khoản</label>
          <input
            type="text"
            placeholder="Nhập tên tài khoản..."
            value={taikhoan}
            onChange={(e) => setTaikhoan(e.target.value)}
            required
          />

          <label>Mật khẩu</label>
          <input
            type="password"
            placeholder="Nhập mật khẩu..."
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {error && <p className="error-text">{error}</p>}

          <button type="submit" className="auth-btn">
            Đăng nhập
          </button>
        </form>

        <p className="switch-text">
          Chưa có tài khoản?{" "}
          <span className="switch-link" onClick={onSwitchToRegister}>
            Đăng ký ngay
          </span>
        </p>
      </div>
    </div>
  );
}

export default Login;
