import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Profile.css";

function Profile({ userId }) {
  const [user, setUser] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  // üîπ C·∫≠p nh·∫≠t ƒë∆∞·ªùng d·∫´n API ƒë√∫ng v·ªõi ASP.NET Core backend
  const API_BASE_URL = "https://localhost:7135/api/users";

  // üîπ L·∫•y th√¥ng tin ng∆∞·ªùi d√πng khi component load
  useEffect(() => {
    if (!userId) return;

    axios
      .get(`${API_BASE_URL}/${userId}`)
      .then((res) => {
        const data = res.data;

        // Map d·ªØ li·ªáu t·ª´ API sang object frontend
        setUser({
          id: data.nguoiDungId || data.id,
          name: data.hoTen || data.name,
          email: data.email || "Ch∆∞a c√≥ email",
          avatar:
            data.anhDaiDien ||
            data.avatar ||
            "/images/avatar.png", // ·∫£nh m·∫∑c ƒë·ªãnh
        });
      })
      .catch((err) => console.error("L·ªói l·∫•y th√¥ng tin ng∆∞·ªùi d√πng:", err));
  }, [userId]);

  // üîπ Khi ng∆∞·ªùi d√πng ch·ªçn file ·∫£nh m·ªõi
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  // üîπ Upload ·∫£nh ƒë·∫°i di·ªán l√™n server
  const handleUpload = async () => {
    if (!selectedFile || !userId) return;

    const formData = new FormData();
    formData.append("avatar", selectedFile);

    try {
      setIsUploading(true);
      const res = await axios.post(
        `${API_BASE_URL}/${userId}/upload-avatar`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      // API tr·∫£ v·ªÅ avatarUrl sau khi upload th√†nh c√¥ng
      setUser((prev) => ({
        ...prev,
        avatar: res.data.avatarUrl || prev.avatar,
      }));

      // Reset preview
      setPreviewUrl(null);
      setSelectedFile(null);
    } catch (err) {
      console.error("L·ªói khi t·∫£i ·∫£nh:", err);
      alert("T·∫£i ·∫£nh th·∫•t b·∫°i!");
    } finally {
      setIsUploading(false);
    }
  };

  if (!user) {
    return (
      <div className="profile-container">
        <div className="profile-card">
          <p>ƒêang t·∫£i th√¥ng tin ng∆∞·ªùi d√πng...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="profile-container">
      <div className="profile-card">
        {/* ·∫¢nh ƒë·∫°i di·ªán */}
        <div className="avatar-wrapper">
          <img
            src={previewUrl || user.avatar}
            alt="Avatar"
            className="profile-avatar"
          />
        </div>

        {/* Th√¥ng tin ng∆∞·ªùi d√πng */}
        <div className="profile-info">
          <h2>{user.name}</h2>
          <p>{user.email}</p>
        </div>

        {/* Upload ·∫£nh */}
        <div className="profile-details" style={{ textAlign: "center" }}>
          <input
            type="file"
            accept="image/*"
            id="avatar-upload"
            onChange={handleFileChange}
            style={{ display: "none" }}
          />
          <label htmlFor="avatar-upload" className="profile-btn">
            Ch·ªçn ·∫£nh
          </label>

          {selectedFile && (
            <button
              onClick={handleUpload}
              disabled={isUploading}
              className="profile-btn"
              style={{ marginLeft: "10px" }}
            >
              {isUploading ? "ƒêang t·∫£i..." : "L∆∞u ·∫£nh"}
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
