import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Login.css";

function Login({ onLogin }) {  // ✅ nhận prop onLogin
  const navigate = useNavigate();
  const [taikhoan, setTaikhoan] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      const res = await axios.post("http://localhost:3000/api/users/login", {
        taikhoan,
        password,
      }).then((res) => {
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("taikhoan", taikhoan);
      });

      if (res.data && (res.data.success || res.data.token)) {
        localStorage.setItem("userId", res.data.user?.taikhoan);
        localStorage.setItem("isLoggedIn", "true");
        navigate("/Profile");

        // ✅ Báo cho App biết là đã đăng nhập
        if (onLogin) onLogin();

        // ✅ Điều hướng sang Profile
        navigate("/Profile");
      } else {
        setIsError(true);
        setMessage(res.data.message || "Đăng nhập thất bại!");
      }
    } catch (err) {
      setIsError(true);
      setMessage(err.response?.data?.message || "⚠️ Lỗi kết nối server!");
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>Đăng nhập</h2>
        <form onSubmit={handleLogin}>
          <label>Tên đăng nhập</label>
          <input
            type="text"
            placeholder="Nhập tên tài khoản..."
            value={taikhoan}
            onChange={(e) => setTaikhoan(e.target.value)}
            required
          />

          <label>Mật khẩu</label>
          <input
            type="password"
            placeholder="Nhập mật khẩu..."
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {message && (
            <p className={`login-message ${isError ? "error" : "success"}`}>
              {message}
            </p>
          )}

          <button type="submit" className="login-btn">
            Đăng nhập
          </button>
        </form>

        <p className="switch-text">
          Chưa có tài khoản?{" "}
          <span className="switch-link" onClick={() => navigate("/Signup")}>
            Đăng ký ngay
          </span>
        </p>
      </div>
    </div>
  );
}

export default Login;
