import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, useNavigate } from "react-router-dom";
import Signup from "./component/Signup.jsx";
import Menu from "./component/Menu.jsx"
import Login from "./component/Login.jsx";
import Profile from "./component/Profile.jsx";
import QuanLyUser from "./component/QuanLyUser.jsx";
import api from "./axiosConfig";

function AppWrapper() {
  // ✅ Vì useNavigate chỉ dùng trong Router, bọc App vào component khác
  return (
    <Router>
      <App />
    </Router>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // ✅ Sử dụng hook

  // ✅ Giữ trạng thái đăng nhập khi reload
  useEffect(() => {
    const loggedIn = localStorage.getItem("isLoggedIn") === "true";
    if (loggedIn) setIsLoggedIn(true);
  }, []);

  // ✅ Khi đăng nhập thành công từ Login.jsx
  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
    navigate("/Profile"); // tự động vào Profile sau login
  };

  // ✅ Khi đăng xuất
  const handleLogout = async () => {
    try {
      await api.post("/logout");
    } catch (err) {
      console.warn("Logout failed:", err.message);
    } finally {
      setIsLoggedIn(false);
      localStorage.removeItem("isLoggedIn");
      navigate("/Login"); // điều hướng bằng useNavigate
    }
  };

  return (
    <>
      {isLoggedIn && <Menu isLoggedIn={isLoggedIn} onLogout={handleLogout} />}

      <Routes>
        <Route
          path="/"
          element={isLoggedIn ? <Navigate to="/Profile" /> : <Navigate to="/Login" />}
        />
        <Route path="/Signup" element={<Signup />} />
        <Route path="/Login" element={<Login onLogin={handleLogin} />} />
        <Route
          path="/Profile"
          element={isLoggedIn ? <Profile /> : <Navigate to="/Login" />}
        />
        <Route
          path="/Quanly"
          element={isLoggedIn ? <QuanLyUser /> : <Navigate to="/Login" />}
        />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </>
  );
}

export default AppWrapper;
