import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axiosConfig";
import "./Profile.css";

function Profile() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({ name: "", email: "", password: "" });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p.");
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get("/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUser(res.data);
        setFormData({ name: res.data.name || "", email: res.data.email || "", password: "" });
      } catch (err) {
        console.error("L·ªói khi l·∫•y th√¥ng tin user:", err);
        if (err.response?.status === 401) {
          setError("Phi√™n ƒëƒÉng nh·∫≠p h·∫øt h·∫°n. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.");
          localStorage.removeItem("token");
        } else {
          setError("Kh√¥ng th·ªÉ t·∫£i th√¥ng tin ng∆∞·ªùi d√πng.");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    setMessage("");
    setSaving(true);
    const token = localStorage.getItem("token");
    if (!token) {
      setError("B·∫°n ch∆∞a ƒëƒÉng nh·∫≠p.");
      setSaving(false);
      return;
    }

    try {
      const res = await axios.put(
        "/profile",
        { ...formData },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUser(res.data);
      setFormData({ ...formData, password: "" }); // reset password input
      setMessage("üéâ C·∫≠p nh·∫≠t th√¥ng tin th√†nh c√¥ng!");
    } catch (err) {
      setMessage(err.response?.data?.message || "‚ùå L·ªói khi c·∫≠p nh·∫≠t th√¥ng tin.");
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="profile-page">
        <div className="loading-card">ƒêang t·∫£i th√¥ng tin ng∆∞·ªùi d√πng...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="profile-page">
        <div className="error-card">{error}</div>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <header className="header">
        <div className="logo" onClick={() => navigate("/")}>
          üè† Trang ch·ªß
        </div>
      </header>

      <main className="profile-content">
        <div className="profile-card">
          <img
            src="https://cdn-icons-png.flaticon.com/512/3135/3135715.png"
            alt="Avatar"
            className="avatar"
          />
          <h2>{user.name || "Kh√¥ng c√≥ t√™n"}</h2>
          <p className="email">{user.email}</p>
          <p className="role">
            Vai tr√≤: <span>{user.role}</span>
          </p>

          <div className="edit-profile">
            <h3>Ch·ªânh s·ª≠a th√¥ng tin</h3>
            <input
              type="text"
              name="name"
              placeholder="H·ªç v√† t√™n"
              value={formData.name}
              onChange={handleChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={formData.email}
              onChange={handleChange}
            />
            <input
              type="password"
              name="password"
              placeholder="M·∫≠t kh·∫©u m·ªõi"
              value={formData.password}
              onChange={handleChange}
            />

            {message && <p className="profile-message">{message}</p>}

            <button onClick={handleSave} className="save-btn" disabled={saving}>
              {saving ? "ƒêang l∆∞u..." : "L∆∞u thay ƒë·ªïi"}
            </button>
          </div>

          <button onClick={handleLogout} className="logout-btn">
            ƒêƒÉng xu·∫•t
          </button>
        </div>
      </main>
    </div>
  );
}

export default Profile;
