import React, { useState } from "react";
import axios from "axios";

function AddUser({ onAdded, onEdit, onDelete }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:3000/users", { name, email });
      alert("✅ Thêm user thành công!");
      setName("");
      setEmail("");
      onAdded();
    } catch (err) {
      console.error("❌ Lỗi khi thêm user:", err);
    }
  };

  const handleEdit = () => {
    if (onEdit) {
      onEdit({ name, email });
    }
  };

  const handleDelete = () => {
    if (onDelete) {
      onDelete({ name, email });
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Tên"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />
      <div style={{ marginTop: "8px" }}>
        <button type="submit" style={{ marginRight: "5px" }}>Thêm</button>
        <button type="button" onClick={handleEdit} style={{ marginRight: "5px" }}>Sửa</button>
        <button type="button" onClick={handleDelete}>Xóa</button>
      </div>
    </form>
  );
}

export default AddUser;
