import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Profile.css";

function Profile({ userId }) {
  const [user, setUser] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  // Lấy thông tin người dùng
  useEffect(() => {
    if (!userId) return;
    axios
      .get(`http://localhost:3000/api/users/${userId}`)
      .then((res) => setUser(res.data))
      .catch((err) => console.error(err));
  }, [userId]);

  // Khi chọn file => tạo URL xem trước
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
    if (file) setPreviewUrl(URL.createObjectURL(file));
  };

  // Upload avatar
  const handleUpload = async () => {
    if (!selectedFile || !userId) return;
    const formData = new FormData();
    formData.append("avatar", selectedFile);

    try {
      setIsUploading(true);
      const res = await axios.post(
        `http://localhost:3000/api/users/${userId}/upload-avatar`,
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );
      setUser((prev) => ({ ...prev, avatar: res.data.avatarUrl }));
      setPreviewUrl(null);
      setSelectedFile(null);
    } catch (err) {
      console.error("Lỗi khi tải ảnh:", err);
      alert("Tải ảnh thất bại!");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <div className="avatar-section">
          <div className="avatar-border">
            <img
              src={previewUrl || user?.avatar || "/images/avatar.png"}
              alt="Avatar"
              className="avatar-image"
            />
          </div>
        </div>

        <h2 className="profile-name">{user?.name || "Người dùng"}</h2>
        <p className="profile-email">{user?.email || "Chưa có email"}</p>

        <div className="upload-section">
          <input
            type="file"
            accept="image/*"
            id="avatar-upload"
            className="hidden-input"
            onChange={handleFileChange}
          />
          <label htmlFor="avatar-upload" className="upload-label">
            Chọn ảnh
          </label>

          {selectedFile && (
            <button
              onClick={handleUpload}
              disabled={isUploading}
              className="upload-btn"
            >
              {isUploading ? "Đang tải..." : "Lưu ảnh"}
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
