import React, { useState, useEffect } from "react";
import axios from "axios";

function AddUser({ onAdded, editingUser, onCancelEdit }) {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  // Khi c√≥ user c·∫ßn s·ª≠a ‚Üí ƒë·ªï d·ªØ li·ªáu v√†o form
  useEffect(() => {
    if (editingUser) {
      setName(editingUser.name);
      setEmail(editingUser.email);
    }
  }, [editingUser]);

  // X·ª≠ l√Ω th√™m/s·ª≠a user
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingUser) {
        // üìù PUT: c·∫≠p nh·∫≠t user
        await axios.put(`http://localhost:3000/users/${editingUser.id}`, {
          name,
          email,
        });
        alert("‚úÖ C·∫≠p nh·∫≠t user th√†nh c√¥ng!");
      } else {
        // ‚ûï POST: th√™m user m·ªõi
        await axios.post("http://localhost:3000/users", { name, email });
        alert("‚úÖ Th√™m user th√†nh c√¥ng!");
      }

      setName("");
      setEmail("");
      onAdded(); // reload l·∫°i danh s√°ch
    } catch (err) {
      console.error("‚ùå L·ªói khi l∆∞u user:", err);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="user-form">
      <input
        type="text"
        placeholder="T√™n ng∆∞·ªùi d√πng"
        value={name}
        onChange={(e) => setName(e.target.value)}
        required
      />
      <input
        type="email"
        placeholder="Email ng∆∞·ªùi d√πng"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
      />

      <div className="btn-group">
        <button type="submit">{editingUser ? "üíæ L∆∞u" : "‚ûï Th√™m"}</button>
        {editingUser && (
          <button type="button" onClick={onCancelEdit} className="cancel-btn">
            ‚ùå H·ªßy
          </button>
        )}
      </div>
    </form>
  );
}

export default AddUser;
