import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Signup from "./component/Signup.jsx";
import Login from "./component/Login.jsx";
import Profile from "./component/Profile.jsx";
import QuanLyUser from "./component/QuanLyUser.jsx";
import EditProfile from "./components/EditProfile";
import ChangePassword from "./components/ChangePassword";
import api from "./axiosConfig";

function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isLoading, setIsLoading] = useState(true); // Thêm trạng thái loading

  // Kiểm tra trạng thái đăng nhập từ server khi tải lại trang
  useEffect(() => {
    const checkAuth = async () => {
      try {
        // Giả sử bạn có API để kiểm tra trạng thái đăng nhập
        const response = await api.get("/check-auth"); // Thay bằng API thực tế
        if (response.data.isAuthenticated) {
          setIsLoggedIn(true);
          localStorage.setItem("isLoggedIn", "true");
        } else {
          setIsLoggedIn(false);
          localStorage.removeItem("isLoggedIn");
        }
      } catch (err) {
        console.warn("Auth check failed:", err.message);
        setIsLoggedIn(false);
        localStorage.removeItem("isLoggedIn");
      } finally {
        setIsLoading(false); // Hoàn tất kiểm tra
      }
    };
    checkAuth();
  }, []);

  // Xử lý đăng nhập
  const handleLogin = () => {
    setIsLoggedIn(true);
    localStorage.setItem("isLoggedIn", "true");
  };

  // Xử lý đăng xuất
  const handleLogout = async () => {
    try {
      await api.post("/logout");
      setIsLoggedIn(false);
      localStorage.removeItem("isLoggedIn");
    } catch (err) {
      console.error("Logout failed:", err.message);
      // Có thể thêm thông báo cho người dùng (VD: toast notification)
      alert("Đăng xuất thất bại, vui lòng thử lại.");
    }
  };

  // Hiển thị loading khi kiểm tra auth
  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <Routes>
      {/* Gộp route "/" và "/Login" thành một */}
      <Route path="/" element={<Login onLogin={handleLogin} />} />
      <Route path="/signup" element={<Signup />} />
      <Route
        path="/profile"
        element={isLoggedIn ? <Profile onLogout={handleLogout} /> : <Navigate to="/" />}
      />
      <Route
        path="/quan-ly" // Sửa tên route cho rõ ràng và đúng chính tả
        element={isLoggedIn ? <QuanLyUser onLogout={handleLogout} /> : <Navigate to="/" />}
      />
      <Route path="*" element={<Navigate to="/" />} />
    </Routes>
  );
}

export default AppWrapper;
