import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "../axiosConfig";
import "./ChangePassword.css";

function ChangePassword() {
  const navigate = useNavigate();
  const [form, setForm] = useState({ oldPassword: "", newPassword: "", confirmPassword: "" });
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  // Lấy mật khẩu cũ từ DB khi component load
  useEffect(() => {
    const fetchOldPassword = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("/users/old-password", {
          headers: { Authorization: `Bearer ${token}` },
        });
        // res.data.oldPassword = giả sử server trả về giá trị
        setForm(prev => ({ ...prev, oldPassword: res.data.oldPassword }));
      } catch (err) {
        console.log("Không thể lấy mật khẩu cũ:", err);
      }
    };
    fetchOldPassword();
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    if (form.newPassword !== form.confirmPassword) {
      setError("Mật khẩu mới không khớp!");
      return;
    }

    const token = localStorage.getItem("token");
    try {
      await axios.put("/change-password", form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSuccess("Đổi mật khẩu thành công!");
      setError("");
      setForm(prev => ({ ...prev, newPassword: "", confirmPassword: "" })); // chỉ reset mật khẩu mới
    } catch (err) {
      setError("Đổi mật khẩu thất bại.");
      setSuccess("");
    }
  };

  return (
    <div className="change-password-page">
      <h2>Đổi mật khẩu</h2>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}
      <form onSubmit={handleSubmit} className="change-password-form">
        <label>
          Mật khẩu cũ:
          <input type="password" name="oldPassword" value={form.oldPassword} onChange={handleChange} />
        </label>
        <label>
          Mật khẩu mới:
          <input type="password" name="newPassword" value={form.newPassword} onChange={handleChange} />
        </label>
        <label>
          Xác nhận mật khẩu mới:
          <input type="password" name="confirmPassword" value={form.confirmPassword} onChange={handleChange} />
        </label>
        <button type="submit">Đổi mật khẩu</button>
      </form>
      <button onClick={() => navigate("/profile")}>⬅️ Quay lại</button>
    </div>
  );
}

export default ChangePassword;
