import React, { useEffect, useState } from "react";
import api from "../axiosConfig"; // ‚úÖ import ƒë√∫ng file axios c·ªßa b·∫°n
import "./Profile.css";

function Profile({ userId }) {
  const [user, setUser] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  // üß† L·∫•y th√¥ng tin ng∆∞·ªùi d√πng t·ª´ MongoDB
  useEffect(() => {
    if (!userId) return;

    api
      .get(`/${userId}`)
      .then((res) => {
        const data = res.data;
        setUser({
          id: data._id,
          name: data.hoTen || data.name || "Ng∆∞·ªùi d√πng",
          email: data.email || "Ch∆∞a c√≥ email",
          avatar: data.anhDaiDien || data.avatar || "/images/avatar.png",
        });
      })
      .catch((err) => console.error("‚ùå L·ªói khi l·∫•y th√¥ng tin user:", err));
  }, [userId]);

  // üì∏ Khi ch·ªçn file => t·∫°o preview
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
    }
  };

  // ‚òÅ Upload avatar l√™n server
  const handleUpload = async () => {
    if (!selectedFile || !userId) return;

    const formData = new FormData();
    formData.append("avatar", selectedFile);

    try {
      setIsUploading(true);

      const res = await api.post(`/${userId}/upload-avatar`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // C·∫≠p nh·∫≠t ·∫£nh sau khi upload
      setUser((prev) => ({
        ...prev,
        avatar: res.data.avatarUrl || prev.avatar,
      }));

      setPreviewUrl(null);
      setSelectedFile(null);
    } catch (err) {
      console.error("‚ùå L·ªói upload ·∫£nh:", err);
      alert("Upload th·∫•t b·∫°i!");
    } finally {
      setIsUploading(false);
    }
  };

  // üß© Loading
  if (!user) {
    return (
      <div className="profile-container">
        <div className="profile-card">
          <p>ƒêang t·∫£i th√¥ng tin ng∆∞·ªùi d√πng...</p>
        </div>
      </div>
    );
  }

  // üßç Hi·ªÉn th·ªã giao di·ªán
  return (
    <div className="profile-container">
      <div className="profile-card">
        {/* Avatar */}
        <div className="avatar-wrapper">
          <img
            src={previewUrl || user.avatar}
            alt="Avatar"
            className="profile-avatar"
          />
        </div>

        {/* Th√¥ng tin ng∆∞·ªùi d√πng */}
        <div className="profile-info">
          <h2>{user.name}</h2>
          <p>{user.email}</p>
        </div>

        {/* Upload ·∫£nh */}
        <div className="profile-details" style={{ textAlign: "center" }}>
          <input
            type="file"
            accept="image/*"
            id="avatar-upload"
            onChange={handleFileChange}
            style={{ display: "none" }}
          />
          <label htmlFor="avatar-upload" className="profile-btn">
            Ch·ªçn ·∫£nh
          </label>

          {selectedFile && (
            <button
              onClick={handleUpload}
              disabled={isUploading}
              className="profile-btn"
              style={{ marginLeft: "10px" }}
            >
              {isUploading ? "ƒêang t·∫£i..." : "L∆∞u ·∫£nh"}
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
