import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Login.css";

function Login({ onLogin }) {
  const navigate = useNavigate();
  const [taikhoan, setTaikhoan] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [isError, setIsError] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage("");
    setIsError(false);
    setLoading(true);

    try {
      const res = await axios.post("http://localhost:3000/users/login", {
        taikhoan,
        password,
      });

      const { token, user } = res.data;

      if (!token || !user) {
        setIsError(true);
        setMessage(res.data.message || "Đăng nhập thất bại!");
        return;
      }

      // Lưu token và thông tin user
      localStorage.setItem("token", token);
      localStorage.setItem("userId", user._id); // lưu _id thay vì taikhoan
      localStorage.setItem("role", user.role);
      localStorage.setItem("isLoggedIn", "true");

      if (onLogin) onLogin();

      // Điều hướng theo role
      if (user.role === "admin") {
        navigate("/admin");
      } else {
        navigate("/profile");
      }
    } catch (err) {
      console.error(err);
      setIsError(true);
      setMessage(err.response?.data?.message || "⚠️ Lỗi kết nối server!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h2>Đăng nhập</h2>
        <form onSubmit={handleLogin}>
          <label>Tên đăng nhập</label>
          <input
            type="text"
            placeholder="Nhập tên tài khoản..."
            value={taikhoan}
            onChange={(e) => setTaikhoan(e.target.value)}
            required
          />

          <label>Mật khẩu</label>
          <input
            type="password"
            placeholder="Nhập mật khẩu..."
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {message && (
            <p className={`login-message ${isError ? "error" : "success"}`}>
              {message}
            </p>
          )}

          <button type="submit" className="login-btn" disabled={loading}>
            {loading ? "Đang đăng nhập..." : "Đăng nhập"}
          </button>
        </form>

        <div className="login-footer">
          <p className="switch-text">
            Chưa có tài khoản?{" "}
            <span className="switch-link" onClick={() => navigate("/Signup")}>
              Đăng ký ngay
            </span>
          </p>
          <p className="forgot-password">
            <p className="switch-text">
                  <span className="switch-link" onClick={() => navigate("/ForgotPassword")}>
              Đăng ký ngay
            </span>
          </p>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
