import React, { useEffect, useState } from "react";
import api from "../axiosConfig"; // bạn đã có file này
import "./Profile.css";

function Profile() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          console.error("Không có token trong localStorage");
          return;
        }

        const res = await api.get("/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUser(res.data);
      } catch (err) {
        console.error("Lỗi khi lấy thông tin user:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  if (loading) {
    return (
      <div className="profile-wrapper">
        <div className="profile-loading">Đang tải thông tin người dùng...</div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="profile-wrapper">
        <div className="profile-error">Không thể tải thông tin người dùng.</div>
      </div>
    );
  }

  return (
    <div className="profile-wrapper">
      <div className="profile-card">
        <div className="avatar-section">
          <img
            src={user.avatar || "/images/avatar.png"}
            alt="Avatar"
            className="avatar-image"
          />
        </div>
        <h2 className="profile-name">{user.name || "Người dùng"}</h2>
        <p className="profile-email">{user.email || "Chưa có email"}</p>
        <p className="profile-role">Vai trò: {user.role || "Chưa rõ"}</p>
      </div>
    </div>
  );
}

export default Profile;
